        uint64 startingHeight,
        uint64 endingHeight
    )
    external
    view
    returns (
        ValidatorSlashEvent[] calldata slashes,
        PageResponse calldata pageResponse
    );

    /// @dev Queries the total rewards accrued by a delegation from a specific address to a given validator.
